import React, {Component} from 'react';
import './style.scss';
import { db } from '../firebaseConfig';
import logo from '../images/logo.svg';
import star from '../images/star.svg';
import monica from '../images/monica.jpeg';
import ridhi from '../images/ridhi.jpg';
import arun from '../images/arun.jpg';
import pankaj from '../images/pankaj.jpeg';
import uday from '../images/uday.jpg';
import ram from '../images/ram.jpeg';

class Home extends Component {
    constructor(props) {
        super(props);
        this.state = {
            teamData: [],
        }
      }
    componentDidMount() {
        const TeamData = db.collection('team').doc('info');
          TeamData.get().then((doc) => {
            if (doc.exists) {
              this.setState({ teamData: doc.data().data });
            } else {
              console.log("No such document!");
            }
          }).catch((error) => {
            console.log("Error getting document:", error);
          });
      }
    render() {
        const { teamData } = this.state;
        return (
            <div className="container">
                <div className="row">
                    <div className="col-lg-12 my-5"><h1>FE Team Members</h1></div>
                </div>
                <div className="row">
                { teamData && teamData.map((item, index) => ( 
                    <div className="col-xs-12 col-sm-6 col-lg-4" key={index}>
                        <figure className="effect-bubba">
                            <img src={monica} className="img-thumbnail img-fluid rounded" />
                            <figcaption className="hidden-sm hidden-xs">
                                <p>Resourceful</p>
                                <h2>{item.name}</h2>
                            </figcaption>
                        </figure>
                    </div>
                ))}
                </div>
            </div>
        );
    }
  }
  export default Home;
